sys_transform_entry.source_script:
answer = (function transformEntry(source) {

	// Add your code here
	var str = source.u_author.toString();

	if (str.length === 0){
		reject = true;
		ignore = true;
		return "";
	}else{
		var firstName = '';
		var surName = ''; 
		firstName = str.split(' ')[0];
        surName = str.indexOf(' ') != -1 ? str.substring(str.indexOf(' ') + 1) :'';
	var grAuthor = new GlideRecord('x_799613_quotes_author');
	grAuthor.addQuery('u_name',firstName);
	grAuthor.addQuery('u_surname',surName);
	grAuthor.query();
	if (grAuthor.next()) {
		return grAuthor.sys_id;
	}else {
		grAuthor = new GlideRecord('x_799613_quotes_author');
		grAuthor.initialize();
		grAuthor.u_name = firstName;
		grAuthor.u_surname = surName;
		grAuthor.insert();
		return grAuthor.sys_id;
		}	
	}
})(source);
