sysauto_script.script:
var currentCategory = gs.getProperty('x_799613_quotes.quotes_current_category'); 
var nowDate = new GlideDateTime();
var grQuote;
	if(!selectQuote(currentCategory, nowDate)){
			gs.info('MBL: Start of selection');
		grQuote = new GlideRecord('x_799613_quotes_quotes');
		grQuote.query();
		grQuote.u_active = true;
			gs.info('MBL: Setting u_active to true');
		grQuote.u_date_active = '';
			gs.info('MBL: Setting u_date_active to empty');
		grQuote.updateMultiple();
			gs.info('MBL: Setting updating all records');
		selectQuote(currentCategory, nowDate);

	}
function selectQuote(currentCategory, nowDate){
	var incList = [];
		gs.info('MBL: Declaring variables for random');
	var grQuote = new GlideRecord('x_799613_quotes_quotes');
	grQuote.addEncodedQuery('u_active=true^u_date_activeISEMPTY^u_category!='+ currentCategory);
		gs.info('MBL: EncodedQuery of required filtered values');
	grQuote.query();
		if (grQuote.hasNext()){
		while (grQuote.next()) {  
			incList.push(grQuote.getValue('sys_id'));
		}
		var random = Math.floor((Math.random() * incList.length));
		var sys_id = incList[random];
		grQuote = new GlideRecord('x_799613_quotes_quotes');
			gs.info('Randomly selecting target Quote');
		grQuote.get(sys_id);
		grQuote.u_active = false;
			gs.info('MBL: Setting u_active to false');
		grQuote.u_date_active = nowDate;
			gs.info('MBL: Setting u_date_active to current date');
		grQuote.update();
			gs.info('Updating Quotes data');
		var grPro = new GlideRecord('sys_properties');
		grPro.addQuery('name','x_799613_quotes.quotes_current_category');
		grPro.query();
			gs.info('Storing category data');
		if (grPro.next()) {
			grPro.value = grQuote.u_category;
			grPro.update();
		}
			gs.info('Updating category data');
		return true;
		}else{
			return false;
		}
}
